//Remember, reducers are functions that take previous state and return new state as a pure function
//Do not mutate existing state

let authReducer = function(code = "o", action){
	switch (action.type){
		case 'EXAMPLE':
			return code + 'updated'
		default:
			return code;
	}
}

export default authReducer;
