import PlayerComponent from './components/ControlsComponent'

import axios from 'axios';
import './App.css';

import actions from './redux/actions';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';

import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

const queryString = require('querystring');

//This component will check to make sure there is a valid access token for the player and, if so, render the player
//If there is an arrow it will return to the Login screen
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      // token: "",
      deviceId: "",
      loggedIn: false,
      error: "",
      // trackName: "Track Name",
      // artistName: "Artist Name",
      // albumName: "Album Name",
      // playing: false,
      // position: 0,
      // duration: 0,
    };
  }
  componentDidMount() {
    const accessToken = this.props.location.state.token || this.props.accessToken
    this.loadPlayer(accessToken);
    spotifyApi.setAccessToken(accessToken);
  }

  handleLogin() {
    // if (this.state.token !== "") {
    //  this.setState({ loggedIn: true });
    //   // check every second for the player.
    //   // this.playerCheckInterval = setInterval(() => this.checkForPlayer(), 1000);
    // }
  }
  async waitForSpotifyWebPlaybackSDKToLoad () {
    return new Promise(resolve => {
      if (window.Spotify) {
        resolve(window.Spotify);
      } else {
        window.onSpotifyWebPlaybackSDKReady = () => {
          resolve(window.Spotify);
        };
      }
    });
  };

  loadPlayer(token){
    (async () => {
      const { Player } = await this.waitForSpotifyWebPlaybackSDKToLoad();
      const sdk = new Player({
        name: "Mike's Test Spotify Player",
        getOAuthToken: cb => { cb(token); },
      });
      this.createEventHandlers(sdk, token);
      let connected = await sdk.connect();
      if (connected) {
        let state = await sdk.getCurrentState();
      }
    })();
  }

  createEventHandlers(player, token) {
    player.on('initialization_error', e => { console.error(e); });
    player.on('authentication_error', e => {
      console.error(e);
      this.setState({ loggedIn: false });
    });
    player.on('account_error', e => { console.error(e); });
    player.on('playback_error', e => { console.error(e); });

    // Playback status updates
    player.on('player_state_changed', state => this.onStateChanged(state));

    // Ready
    player.on('ready', async data => {
      let { device_id } = data;
      console.log('ready', device_id)
      console.log("Let the music play on!");
      await this.setState({ deviceId: device_id });
      this.transferPlaybackHere(device_id, token);
      // this.player.connect();
    });
  }
  onStateChanged(state) {
    // if we're no longer listening to music, we'll get a null state.
    if (state !== null) {
      const {
        current_track: currentTrack,
        position,
        duration,
      } = state.track_window;
      const trackName = currentTrack.name;
      const albumName = currentTrack.album.name;
      const artistName = currentTrack.artists
        .map(artist => artist.name)
        .join(", ");
      const playing = !state.paused;
      this.setState({
        position,
        duration,
        trackName,
        albumName,
        artistName,
        playing
      });
    }
  }

  transferPlaybackHere(deviceId, token) {
    //Probably should update this with axios later
    fetch("https://api.spotify.com/v1/me/player", {
      method: "PUT",
      headers: {
        authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        "device_ids": [ deviceId ],
        "play": true,
      }),
    });
  }
  render(){
    if (this.props.accessToken){
      return <div>Access code: {this.props.accessToken}</div>
    } else {
      return <Redirect to="/login" />
    }
  }
  // render() {
