  //       {error && <p>Error: {error}</p>}
  //
  //       {loggedIn ?
  //       (<div>
  //         <SearchComponent/>
  //         <p>Artist: {artistName}</p>
  //         <p>Track: {trackName}</p>
  //         <p>Album: {albumName}</p>
  //         <ControlsComponent playing={playing} player={this.player}/>
  //       </div>)
  //       :
  //       (<div>
  //         <p>
  //           <LoginComponent />
  //           {token}
  //         </p>
  //       </div>)
  //       }
  //       <div>Access code: {this.props.accessToken}</div>
  //       </div>
  //     </div>
  //   );
  // }
}

